// Centralized navigation utilities
export const routes = {
  home: '/',
  landing: '/landing',
  login: '/login',
  dashboard: '/dashboard',
  documents: '/documents',
  directory: '/directory',
  knowledge: '/knowledge',
  settings: '/settings',
  admin: '/admin',
  auth: {
    callback: '/auth/callback',
    error: '/auth/error',
  },
} as const;

export function getDefaultRouteForUser(isAuthenticated: boolean): string {
  return isAuthenticated ? routes.dashboard : routes.landing;
}

export function isProtectedRoute(pathname: string): boolean {
  const publicRoutes = [routes.landing, routes.login, routes.auth.callback];
  return !publicRoutes.some(route => pathname.startsWith(route));
}

export function getRedirectUrl(from: string, isAuthenticated: boolean): string {
  if (!isAuthenticated && isProtectedRoute(from)) {
    return `${routes.login}?redirect=${encodeURIComponent(from)}`;
  }
  return from;
}

