const fs = require('fs');
const path = require('path');
const glob = require('glob');

// Script to update all home links to new routing
const files = glob.sync('src/**/*.{ts,tsx}', {
  ignore: ['**/node_modules/**']
});

let updatedCount = 0;
const replacements = [
  // Update home links in navigation
  { from: 'href="/"', to: 'href="/dashboard"' },
  { from: "href='/'>", to: "href='/dashboard'>" },

  // Update router.push calls
  { from: "router.push('/');", to: "router.push('/dashboard');" },
  { from: 'router.push("/");', to: 'router.push("/dashboard");' },

  // Update Link components
  { from: '<Link href="/">', to: '<Link href="/dashboard">' },

  // Landing CTAs
  { from: 'Get Started', to: 'Sign In' },
  { from: 'href="#demo"', to: 'href="/login"' },
];

files.forEach(file => {
  let content = fs.readFileSync(file, 'utf8');
  const originalContent = content;

  replacements.forEach(({ from, to }) => {
    if (content.includes(from)) {
      content = content.replace(new RegExp(from, 'g'), to);
    }
  });

  if (content !== originalContent) {
    fs.writeFileSync(file, content);
    console.log(` Updated: ${file}`);
    updatedCount++;
  }
});

console.log(`\n Updated ${updatedCount} files with new routing structure.`);

