'use client';

import { useEffect, useRef } from 'react';
import { Niivue } from '@niivue/niivue';

interface NiivueViewerProps {
  mode?: '3d' | 'axial' | 'sagittal' | 'coronal';
  opacity?: number;
  colormap?: string;
}

export function NiivueViewer({ 
  mode = '3d', 
  opacity = 0.8, 
  colormap = 'gray' 
}: NiivueViewerProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const nvRef = useRef<Niivue | null>(null);

  useEffect(() => {
    if (!canvasRef.current) return;

    // Initialize Niivue
    const nv = new Niivue({
      backColor: [0, 0, 0, 1],
      show3Dcrosshair: true,
    });

    nvRef.current = nv;
    nv.attachToCanvas(canvasRef.current);

    // Load demo volume
    nv.loadVolumes([{
      url: '/brain-scans/mni152.nii.gz',
      opacity: opacity,
      colormap: colormap,
    }]);

    // Set view mode
    if (mode === '3d') {
      nv.setRenderAzimuthElevation(120, 15);
    } else {
      nv.setSliceType(mode === 'axial' ? 0 : mode === 'sagittal' ? 1 : 2);
    }

    return () => {
      nv.removeCanvas();
    };
  }, []);

  // Update properties
  useEffect(() => {
    if (nvRef.current && nvRef.current.volumes.length > 0) {
      nvRef.current.setOpacity(0, opacity);
      nvRef.current.setColormap(colormap);
    }
  }, [opacity, colormap]);

  return (
    <canvas 
      ref={canvasRef} 
      className="h-full w-full"
      style={{ touchAction: 'none' }}
    />
  );
}
